#[derive(Debug, PartialEq, Eq, Clone, Copy)]
pub enum Network {
    Mainnet,
    Testnet,
    Regtest,
}

const MAGIC_MAINNET: u32 = 0xE8F3E1E3;
const MAGIC_TESTNET: u32 = 0xF4F3E5F4;
const MAGIC_REGTEST: u32 = 0xFABFB5DA;

/// Network magic type.
pub type Magic = u32;

impl Network {
    pub fn magic(&self) -> Magic {
        match *self {
            Network::Mainnet => MAGIC_MAINNET,
            Network::Testnet => MAGIC_TESTNET,
            Network::Regtest => MAGIC_REGTEST,
        }
    }

    pub fn port(&self) -> u16 {
        match *self {
            Network::Mainnet => 8333,
            Network::Testnet => 18333,
            Network::Regtest => 18444,
        }
    }

    pub fn rpc_port(&self) -> u16 {
        match *self {
            Network::Mainnet => 8332,
            Network::Testnet => 18332,
            Network::Regtest => 18443,
        }
    }

    pub fn genesis(&self) -> &str {
        match *self {
            Network::Mainnet => "",
            Network::Testnet => "",
            Network::Regtest => "",
        }
    }
}
